REPLACE INTO `work_exp` (`id`, `userID`, `detail`, `sortOrder`, `status_show`) VALUES
	(22, 66, '<p><strong>ประสบการณ์การสอน</strong></p>\n<ul>\n<li><strong>หลักสูตรวิทยาศาสตร์บัณฑิต</strong> <em>สาขาวิชาเทคโนโลยีสารสนเทศ</em>\n<ul>\n<li>วิชาเทคโนโลยีสารสนเทศเบื้องต้น</li>\n<li>วิชาการออกแบบเว็บไซต์</li>\n<li>วิชาคอมพิวเตอร์กราฟิก</li>\n<li>วิชามัลติมีเดีย</li>\n<li>วิชาแอนิเมชั่น</li>\n<li>วิชาการเขียนโปรแกรมคอมพิวเตอร์</li>\n<li>วิชาการบริหารศูนย์สารสนเทศ</li>\n<li>วิชาระเบียบวิธีวิจัย</li>\n<li>วิชาองค์ประกอบและสถาปัตยกรรมคอมพิวเตอร์</li>\n<li>วิชาการประมวลผลรูปภาพ</li>\n</ul>\n</li>\n<li><strong>หลักสูตรวิทยาศาสตรมหาบัณฑิต </strong><em>สาขาวิชาเทคโนโลยีสารสนเทศ</em>\n<ul>\n<li>วิชาสัมมนา 1 และ 2</li>\n<li>วิชาการบริหารจัดการเทคโนโลยีสารสนเทศ</li>\n<li>วิชาระเบียบวิธีวิจัย</li>\n<li>วิชาการประมวลภาพและคอมพิวเตอร์วิทัศน์</li>\n<li>วิชาระบบค้นคืนสารสนเทศ</li>\n</ul>\n</li>\n</ul>\n<p>&nbsp;</p>', 1, 0),
	(23, 111, '<p>16 สิงหาคม 2551 รวมระยะปฏิบัติงานจนถึงปัจจุบัน 9 ปี</p>', 1, 0),
	(24, 131, '<p>March 2014- present มหาวิทยาลัยอุบลราชธานี อาจารย์ประจำ<br />Feb 2007 - present Safety and Health at Work Promotion Association &nbsp;(Thailand) วิทยากร<br />June 2006 - Mar 2009 มหาวิทยาลัยราชภัฏบ้านสมเด็จเจ้าพระยา อาจารย์พิเศษ<br />May 2001 - Feb 2009 ศูนย์เทคโนโลยีอิเล็กทรอนิกส์และคอมพิวเตอร์แห่งชาติ (NECTEC) วิศวกร<br />May 2000 - Apr 2001 WongPin Co.,Ltd จป.วิชาชีพ</p>', 1, 0),
	(25, 59, '<ul>\n<li>Lecturer at Department of Chemistry, Faculty of Science, Ubon Ratchathani University since 2012 - 2016</li>\n<li>Assistant Professor&nbsp;at Department of Chemistry, Faculty of Science, Ubon Ratchathani University since 2016 - 2020</li>\n<li>Associate Professor&nbsp;at Department of Chemistry, Faculty of Science, Ubon Ratchathani University since 2020</li>\n</ul>', 1, 0),
	(26, 7, '<p>ด้านการบริหาร</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;31 สิงหาคม 2553 - สิงหาคม 2557 : ประธานคณะกรรมการบริหารหลักสูตรระดับบัณฑิตศึกษา&nbsp;สาขาวิชาเทคโนโลยีสารสนเทศ คณะ &nbsp; &nbsp; &nbsp; วิทยาศาสตร์</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;27 พฤษภาคม 2557 &ndash; 30 กันยายน 2558 : ผู้ช่วยคณบดีฝ่ายวิชาการ คณะวิทยาศาสตร์</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 9 กันยายน 2558 &ndash; ปัจจุบัน : อาจารย์ประจำหลักสูตรและอาจารย์ผู้รับผิดชอบหลักสูตรระดับบัณฑิตศึกษา สาขาวิชาเทคโนโลยีสารสนเทศ คณะวิทยาศาสตร์</p>\n<p>&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;30 มิถุนายน 2559 &ndash; ปัจจุบัน : อาจารย์ประจำหลักสูตรวิทยาศาสตรมหาบัณฑิตและหลักสูตรปรัชญาดุษฎีบัณฑิต สาขาวิชาวิทยาศาสตร์ศึกษา &nbsp;คณะวิทยาศาสตร์&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\n<p>ด้านการเรียนการสอน</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 27 พฤษภาคม 2553- ปัจจุบัน &nbsp;: อาจารย์ประจำภาควิชาคณิตศาสตร์ สถิติและคอมพิวเตอร์ คณะวิทยาศาสตร์</p>', 1, 0),
	(27, 121, '<p>พ.ศ. 2564 &ndash; ปัจจุบัน&nbsp; &nbsp; รองหัวหน้าภาควิชาเคมี คณะวิทยาศาสตร์</p>\n<p>พ.ศ. 2560 &ndash; ปัจจุบัน&nbsp; &nbsp; ผู้ช่วยศาสตราจารย์ สังกัดภาควิชาเคมี คณะวิทยาศาสตร์</p>\n<p>พ.ศ. 2559 &ndash; 2560&nbsp; &nbsp; &nbsp;รองหัวหน้าภาควิชาเคมี คณะวิทยาศาสตร์</p>\n<p>พ.ศ. 2558 &ndash; 2562&nbsp; &nbsp; &nbsp;ประธานหลักสูตร วท.บ. เทคโนโลยียางและพอลิเมอร์</p>\n<p>พ.ศ. 2556 &ndash; 2559&nbsp; &nbsp; &nbsp;อาจารย์ สังกัดภาควิชาเคมี คณะวิทยาศาสตร์</p>', 1, 0),
	(28, 330, '<p><span style="background-color: #ffffff; color: #626262;">2015 &ndash; 2020:&nbsp;</span>Lecturer at Faculty of Science, Ubon Ratchathani University&nbsp;</p>', 2, 0),
	(29, 330, '<p><span style="background-color: #ffffff; color: #626262;">2010 - 2013:&nbsp;</span>Independent Researcher at Thailand Environment Institute (TEI), Nonthaburi, Thailand&nbsp;</p>', 3, 0),
	(30, 330, '<p><span style="background-color: #ffffff; color: #626262;">2009 - 2010:&nbsp;</span>Senior Research Associate at Thailand Environment Institute (TEI), Nonthaburi, Thailand</p>', 4, 0),
